import { JsonRpc } from "eosjs";
import { deserialize, ObjectSchema } from "atomicassets";
import React, { StrictMode, useEffect, useState } from "react";
import {
  Button,
  Card,
  CardMedia,
  CardHeader,
  Typography,
  CardContent,
  Grid,
} from "@material-ui/core";
import Form from "react-bootstrap/Form";
import "bootstrap/dist/css/bootstrap.min.css";
import "./droplist.css";
import { makeStyles } from "@material-ui/styles";

const defaultState = {
  activeUser: null,
  accountName: "",
  dropItems: "",
  drops: [],
  statusDisplay: "",
};

var ourNetwork = {
  chainId: "1064487b3cd1a897ce03ae5b6a865651747e2e152090f99c1d19d44e01aea5a4",
  rpcEndpoints: [{ protocol: "https", host: "chain.wax.io", port: "" }],
};

const picture = {
  height: "250px",
  width: "150px",
};

const cardstyleGreen = {
  border: 10,
  borderColor: "green",
  borderStyle: "solid",
};

const cardstyleRed = {
  border: 10,
  borderColor: "red",
  borderStyle: "solid",
};


const WaxUsd = 0.13;

class DropListApp extends React.Component {
  static displayName = "BCH Draco collection";

  constructor(props) {
    super(props);
    this.state = {
      ...defaultState,
      rows: [],
      drops: [],
      ctr: 0,
      have: 0,
      rpc: new JsonRpc(
        `${ourNetwork.rpcEndpoints[0].protocol}://${ourNetwork.rpcEndpoints[0].host}`
      ),
    };

    this.renderModalButton = this.renderModalButton.bind(this);
    this.handleDropUpdate = this.handleDropUpdate.bind(this);
    this.searchDrop = this.searchDrop.bind(this);

    this.renderDropForm = this.renderDropForm.bind(this);
    this.buyDrop = this.buyDrop.bind(this);
    this.buyClaimContract = this.buyClaimContract.bind(this);

    this.partition = (str, n) => {
      var aa = [];
      var i, l;
      for (i = 0, l = str.length; i < l; i += n) {
        aa.push(str.substr(i, n));
      }
      return aa;
    };
  }

  componentDidMount() {
    console.log("run");

    this._asyncRequest = this.state.rpc
      .get_table_rows({
        json: true,
        code: "atomicassets",
        scope: "dracodice",
        table: "templates",
        lower_bound: "356879",
        limit: 1000,
        reverse: false,
        show_payer: false,
      })
      .then((resp) => {
        this._asyncRequest = null;

        var ctr = 0;

        for (let itemNdx in resp.rows) {
          let item = resp.rows[itemNdx];

          if (item.schema_name == "bcheroes1") {
            ctr++;

            let os = ObjectSchema([
              { name: "name", type: "string" },
              { name: "img", type: "image" },
              { name: "series", type: "string" },
              { name: "rarity", type: "string" },
              { name: "material", type: "string" },
              { name: "style", type: "string" },
              { name: "face values", type: "string" },
              { name: "gameimg", type: "image" },
              { name: "video", type: "string" },
              { name: "shape", type: "string" },
            ]);
            let deserializedData = deserialize(
              item.immutable_serialized_data,
              os
            );

            item.name = deserializedData.name;
            item.numberHad = 0;

            // console.log(item);

            item.imgTag = "https://ipfs.io/ipfs/" + deserializedData.img;

            this.state.drops.push(item);
          }
        }

        this.setState((state) => ({
          ...this.state,
          ctr: ctr,
          drops: this.state.drops,
        }));
      });
  }

  componentWillUnmount() {
    if (this._asyncRequest) {
      this._asyncRequest.cancel();
    }
  }

  async buyDrop(buyId, pri) {
    this.buyClaimContract(buyId, pri);
  }

  componentDidUpdate() {
    const {
      //  ual: { activeUser },
    } = this.props;

    //    this.setState(defaultState);
  }

  renderLogoutBtn = () => {};

  renderModalButton() {
    return <p className="ual-btn-wrapper"></p>;
  }

  handleDropUpdate = (event) => {
    let etv = event.target.value;

    this.setState((state) => ({
      ...this.state,
      dropItems: etv,
    }));
  };

  searchDrop = async (event) => {
    var lb = "0";

    var newAsset = "1";

   var haveCollection = {}



    while (lb != newAsset)
    {

    newAsset = lb;

    let resp = await this.searchAssets(lb);

    for (let itemNdx in resp.rows) {
      {
        lb = resp.rows[itemNdx].asset_id;

        var tid = resp.rows[itemNdx].template_id;

        const searchIndex = this.state.drops.findIndex(
          (d) => d.template_id == resp.rows[itemNdx].template_id
        );

        if (searchIndex >= 0) {

         let itm = this.state.drops[searchIndex];
         itm.numberHad++;

         haveCollection[tid] = 1;
          this.state.drops[searchIndex] = itm;
         this.setState((state) => ({...this.state, have: Object.keys(haveCollection).length, drops: this.state.drops}))

        }
      }
    }


  }
  };

  async searchAssets(startingAsset) {
    let resp = await this.state.rpc.get_table_rows({
      json: true,
      code: "atomicassets",
      scope: this.state.dropItems,
      table: "assets",
      lower_bound: startingAsset,
      limit: 1000,
      reverse: false,
      show_payer: false,
    });

    return resp;
  }

  async buyClaimContract(buyId, pri) {
    const { accountName, activeUser } = this.state;

    const roundOne = (n, d) =>
      Math.round(n * Math.pow(10, d)) / Math.pow(10, d);

    var buyString = pri;

    var delphi = 0;

    if (buyString.includes("USD")) {
      //For drops that are USD, need to convert the USD price to WAXP.  Using the estimated hard coded exchange rate from WaxUsd.  Will fail if this is not close enough.

      var priAmt = buyString.split(" ")[0];

      var waxP = priAmt / WaxUsd;

      var waxP = waxP + 0.5;

      var waxP = roundOne(waxP, 8);

      var s = waxP.toString();
      if (s.indexOf(".") == -1) s += ".";

      while (s.length < s.indexOf(".") + 9) s += "0";

      waxP = s;

      waxP = waxP + " WAX";

      delphi = WaxUsd;
    }

    const orderTransaction = {
      actions: [
        {
          account: "eosio.token",
          name: "transfer",
          authorization: [
            {
              actor: accountName,
              permission: activeUser.requestPermission,
            },
          ],
          data: {
            from: accountName,
            to: "atomicdropsx",
            quantity: buyString,
            memo: "deposit",
          },
        },
      ],
    };

    try {
      const account = await this.state.rpc.get_account(accountName);

      await activeUser.signTransaction(orderTransaction, {
        blocksBehind: 3,
        expireSeconds: 15,
      });

      const claimTransaction = {
        actions: [
          {
            account: "atomicdropsx",
            name: "claimdrop",
            authorization: [
              {
                actor: accountName,
                permission: activeUser.requestPermission,
              },
            ],
            data: {
              claimer: accountName,
              drop_id: buyId,
              claim_amount: 1,
              intended_delphi_median: delphi,
              referrer: "atomicdrop",
              country: "US",
            },
          },
        ],
      };

      await activeUser.signTransaction(claimTransaction, {
        blocksBehind: 3,
        expireSeconds: 15,
      });
    } catch (error) {
      console.warn(error);
    }
  }

  renderDropForm = () => {
    const { dropItems } = this.state;

    const part = this.partition;

    const rpc = this.state.rpc;

    const bd = this.buyDrop;

    return (
      <div
        style={{
          marginLeft: "auto",
          marginRight: "auto",
          width: "25%",
          marginTop: "40px",
          marginBottom: "10px",
        }}
      >

        <h2>Draco Dice BCH Status</h2>


        <Form>
          <Form.Group controlId="dropItems">


            <div style={{ width: "150%" }}>
              <div style={{ width: "300px", float: "left" }}>
                <Form.Control
                  type="text"
                  name="dropItems"
                  placeholder="Wallet"
                  value={dropItems}
                  onChange={this.handleDropUpdate}
                />
              </div>
              <div style={{ marginLeft: "305px" }}>
                <Button onClick={this.searchDrop} variant="contained">
                  Search
                </Button>
              </div>
            </div>
          </Form.Group>

          <Typography variant="subtitle2" component="h2">
            {this.state.statusDisplay}
          </Typography>
        </Form>

        <div
          style={{
            textAlign: "center",
            marginLeft: "-250px",
            paddingBottom: "50px",
            paddingTop: "25px",
            width: "900px",
          }}
        >
          <h3>BCH Draco Dice (Have {this.state.have} of {this.state.ctr})</h3>
          <Grid container spacing={4}>
            {this.state.drops.map(function (item, i) {
              var desc = item.name;

              var have = item.numberHad;

              var d1 = new Date(item.start_time * 1000);

              let img = "";

              if (item.imgTag != null && item.imgTag.length > 0) {
                img = <img style={picture} src={item.imgTag} />;
              }

              return (
                <Grid item xs={12} sm={6} md={4} key={item.drop_id}>
                  <Card style={have > 0 ? cardstyleGreen : cardstyleRed}>
                    <CardHeader
                      title={item.collection_name ?? ""}
                      subheader={item.drop_id ?? ""}
                    />
                    <CardContent>
                      {img}
                      <Typography
                        variant="body2"
                        color="textSecondary"
                        component="p"
                      >
                        {desc}<br/>Number of Assets: {have}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              );
            })}
          </Grid>
        </div>
      </div>
    );
  };

  render() {


    return (

        this.renderDropForm()  
    );
  }
}

export default DropListApp;
